    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        try {

            String name = name_tf.getText();
            String mobile = mobile_tf.getText();
            String email = email_tf.getText();
            String salary = salary_tf.getText();

            Class.forName("com.mysql.cj.jdbc.Driver");

            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost:3306/Company", "root", "Mysql@123");

            PreparedStatement ps = cn.prepareStatement("insert into Employee(Name,Mobile_No,Email,Salary) value(?,?,?,?)");

            ps.setString(1, name.toUpperCase());

            if (mobile.length() == 10) {

                ps.setString(2, mobile);

            } else {

                System.out.println("Invalid Mobile Number...!");
            }

            if (email.contains("@gmail.com")) {

                ps.setString(3, email);

            } else {

                System.out.println("Invalid Email ID...!");
            }

            ps.setString(4, salary);

            boolean b = ps.execute();

            if (b == false) {

                JOptionPane.showMessageDialog(rootPane, "Register Successful");

            }

            cn.close();

        } catch (Exception e) {

            System.out.println(e.toString());

        }

    }//GEN-LAST:event_jButton1ActionPerformed

