    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        try {

            String name = new_name.getText();
            String email = new_email.getText();
            String mobile = new_mobile.getText();
            String salary = new_salary.getText();
            String id = id_tf.getText();

            Class.forName("com.mysql.cj.jdbc.Driver");

            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost:3306/Company", "root", "Mysql@123");

            PreparedStatement ps = cn.prepareStatement("update Employee set name = ?, mobile_no = ?, email = ?, salary = ? where id = ?");

            ps.setString(1, name.toUpperCase());

            if (mobile.length() == 10) {

                ps.setString(2, mobile);

            } else {

                System.out.println("Invalid Mobile Number...!");
            }

            if (email.contains("@gmail.com")) {

                ps.setString(3, email);

            } else {

                System.out.println("Invalid Email ID...!");
            }

            ps.setString(4, salary);

            ps.setString(5, id);

            boolean b = ps.execute();

            if (b == false) {

                JOptionPane.showMessageDialog(rootPane, "Update Successful");

            }

            cn.close();

        } catch (Exception e) {

            System.out.println(e.toString());

        }
    }//GEN-LAST:event_jButton4ActionPerformed

