    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed

        try {

            String id = id_tf.getText();

            Class.forName("com.mysql.cj.jdbc.Driver");

            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost:3306/Company", "root", "Mysql@123");

            PreparedStatement ps = cn.prepareStatement("select * from Employee where id = ?");
            ps.setString(1, id);

            ResultSet rs = ps.executeQuery();

            while (rs.next()) {
                String name = rs.getString("name");
                String email = rs.getString("email");
                String mobile = rs.getString("mobile_no");
                String salary = rs.getString("salary");

                new_name.setText(name);
                new_email.setText(email);
                new_mobile.setText(mobile);
                new_salary.setText(salary);

            }

            cn.close();

        } catch (Exception e) {

            System.out.println(e.toString());

        }
    }//GEN-LAST:event_jButton3ActionPerformed

